import React, { Component } from 'react';
import { SendDirectMessage } from './SendDirectMessage.js'
export class DirectMessage extends Component {
	 constructor() {
        super();
       this.state = {
      	messages: [{sender: 		"deepakabs",
      				senderName:		"Deepak ABS",
      				profile_img_url: "abs",
      				sentAt: 		"Wed Nov 20, 2018 at 11",
      				message: 		"1"
      			  }, 
      			  {sender: 		    "deepakabs",
      				senderName:		"Deepak ABS",
      				profile_img_url: "abs",
      				sentAt: 		"Wed Nov 20, 2018 at 11",
      				message: 		"2"
      			  }
      			  ,
      			  {sender: 		"deepakabs",
      				senderName:		"Deepak ABS",
      				profile_img_url: "abs",
      				sentAt: 		"Wed Nov 20, 2018 at 11",
      				message: 		"3"
      			  }, 
      			  {sender: 		    "deepakabs",
      				senderName:		"Deepak ABS",
      				profile_img_url: "abs",
      				sentAt: 		"Wed Nov 20, 2018 at 11",
      				message: 		"4"
      			  }]
      }		  


    }


    // Update Messages
    update_direct_messages(message) {
	    // e.preventDefault();
	    this.setState({
	      messages: this.messages.unshift(message)
	    })
  	}


    // Scroll to bottom
    scrollToBottom = () => {
	  this.messagesEnd.scrollIntoView({ behavior: "smooth" });
	}


	componentDidMount() {
	  this.scrollToBottom();
	}

	componentDidUpdate() {
	  this.scrollToBottom();
	}
	render() {

		return (
			<div className="direct-message-history-wrapper">
			<div className="direct-message-history">

		 	   {this.state.messages.map((message) => 
		 		<Message update_messages = {this.update_direct_messages} message = {message} sender={this.props.sender}  receiver={this.props.receiver}> </Message>
		 		)}
		 		 <div style={{ float:"left", clear: "both" }}
		             ref={(el) => { this.messagesEnd = el; }}>
		        </div>
			</div>

			<hr/>

			<SendDirectMessage sender={this.props.sender} receiver={this.props.receiver}/> 
			</div>
		)
	}
}

class Message extends Component {

	constructor() {
		super();
	}
	render() {
	 	if (this.props.sender.username == this.props.message.sender) {

		return (
				// If message is sent by user style it differently
				<React.Fragment>
				<ul className="sent-message-ul">
					<li>
						{this.props.sender.profile_image_url}
						<br/>
						You
					</li>
					<li className="sent-message"> 
						{this.props.message.message}
						<br/>
						{this.props.message.createdAt}
					</li>

				</ul>		
				</React.Fragment>

	 		)
		}

	}	
}
