import React, { Component } from 'react';
import logo from './logo.svg';
import green_circle from './images/green_circle.png';
import './App.css';
import ReactDOM from 'react-dom';
import { DirectMessage } from './components/DirectMessage.js';
//import { Link, NavLink, Route } from 'react-router-dom' 
import { Switch, Link, NavLink, Route } from 'react-router-dom'




class App extends Component {
   constructor () {

    super()

    // Initialize local variable
    this.selected_member_to_message = "";
    this.current_user                = "";

    // Declare DirectMessageUsers
    this.state = {
      // Set currentUser
      users:                          [],
      // Show loading spinner 
      fetching_direct_message_counts: false,

      // Indicate failure or success of request
      fetched_members_success:        true,

      // Display Current User at top
      currentUser:                    {
                                        name: "DeepakABS",
                                        profile_img_url: "ABS",
                                        username:   "deepakabs"    
                                      },
      // Display 
      members: [],
      selected_member_status: "All",
      member_statuses:        ["All", "Online", "Away", "Offline"],
      test:                   1
    }

     this.selectMemberToMessage = this.selectMemberToMessage.bind(this);         

  }

  // Fetch Users in Company
   componentDidMount() {
      // Show loading 
      this.setState({fetching_members: true});

      // Set currentUser 
      this.fetchUsers()
      .then((users) => {

        console.log ("Nemam Amma Bhagavan Sharanam -- users", users);
        this.setState({
            test:           3,
            users:        users,
        })

        this.current_user = users[0];

        return this.fetchDirectMessageCountForUser(this.current_user)

      })
      .then((user_msg_counts) => {
        console.log("Nemam Amma Bhagavan Sharanam -- members", user_msg_counts)
        this.setState({
            members: user_msg_counts,
            fetching_members: false
        });
        this.fetching_direct_message_counts = false;

      })
     .catch((err) => {
        this.setState({fetching_members: false, fetched_members_success: false})
      });
  }   


  /*
    @name: fetchUsers
    @descr: Collect all users 
  */  
  fetchUsers(){
    let api     = "/api/getUsers/";

    return fetch(api)
    .then(response => response.json());
  }

  /*
    @name: fetchDirectMessageCountForUser
    @descr: Update state user_msg_counts with users and message counts
 */
  fetchDirectMessageCountForUser(currentUser) {

  // Username
  const username = encodeURIComponent(currentUser.username);

  // Company
  const company = encodeURIComponent(currentUser.company);

  let api = "/api/getDirectMessagesCountForUser?username="  + username + "&company=" + company;


  return fetch(api)
  .then(response =>  response.json())

 }

  selectMemberToMessage(member) {
    this.selected_member_to_message = member;
  }
  // Render the Information for each user 
  render() {

    var memberList;

    // Spinner
    if(this.state.fetching_members) {
        memberList = <i className="fa fa-spinner"> </i>;      
     }
    // Error
    else if (!this.state.fetched_members_success) {
       memberList =  <h4> Unable to get members </h4>;
       memberList +=  <button> Retry </button>;
     }

     else  {
        memberList =   this.state.members.map((member) => 
                          <NavLink className="direct-message-list" activeClassName="active" to='/directMessage/member.username'>
                            <tr onClick={this.selectMemberToMessage(member)}>
                              <td> <img src={member.profile_img_url} style={{height:"30px", width:"30px"}}/> </td>
                              <td> {member.name} ({member.new_msg_count}</td>
                              <td> <img src={green_circle}/> </td>
                            </tr>
                            </NavLink>
                        );   
     }

      console.log("Nemam Amma Bhagavan Sharanam -- memberList", memberList);

    return (
      <div className="row">
        <div className="mdl-grid">
          <div className="mdl-cell mdl-cell--3-col nav">
              <select className="mdl-textfield__input" value={this.currentUser.name}> 
                { this.state.users.map((user) => 
                  <option name={user.username} value={user.username}> {user.name} </option>
                )}
                </select>
              <div className="direct-messages">
                  <h4> Direct Messages </h4>
                  <table>
                      <tbody>
                        <tr>
                          <td> </td> <td></td>
                          <td> 
                           <select  className="mdl-textfield__input" value={this.state.selected_member_status} onChange={this.selectMemberStatus}>
                             {this.state.member_statuses.map((status) => 
                                <option name={status} value={status}> {status} </option>
                              )}
                            </select>
                          </td> 
                        </tr>
                        {memberList}
                      </tbody>
                    </table>
              </div>
           </div>

            <div className="mdl-cell mdl-cell--8-col">
                <Route path='/directMessage' 
                 render={(props) => <DirectMessage {...props} sender={this.current_user} receiver={this.selected_member_to_message} />} />
            </div>


       </div>   
    </div> 
    );
  }
}


export default App;
