import React, { Component } from 'react';
import logo from './logo.svg';
import green_circle from './images/green_circle.png';
import './App.css';
import ReactDOM from 'react-dom';
import { DirectMessage } from './components/DirectMessage.js';
//import { Link, NavLink, Route } from 'react-router-dom' 




class App extends Component {
   constructor () {

    super()

    // Initialize local variable
    this.selected_member_to_message = "";
    // Declare DirectMessageUsers
    this.state = {
      // Display Current User at top
      currentUser: {name: "Nemam Amma Bhagavan Sharanam", 
                    username: "deepakabs",
                    profile_img_url: "ABS"  
                  },
      // Display 
      members: [{name:           "Nemam Amma Bhagavan Sharanam",
                 username:        "abs",
                profile_img_url: "Nemam Amma Bhagavan",
                new_msg_count:    1,
                isOnline:         true,
            },
             {name:           "Nemam Amma Bhagavan Sharanam",
              username:        "abs",
              profile_img_url: "Nemam Amma Bhagavan",
              new_msg_count:    1,
              isOnline:         true,
            }],
      selected_member_status: "All",
      member_statuses:        ["All", "Online", "Away", "Offline"]      
    }
  }

  // Fetch Users in Company
   componentDidMount() {

      fetchUsers("defaultCompany").then((users) => {
        this.setState({
          users:  users,
          currentUser: users[0]
        })
      });

      fetchUsersDirectMessageCount()
    // fetchPosts().then(response => {
    //   this.setState({
    //     posts: response.posts
    //   });
    // });
  }
  /*
    @name: fetchUsers
    @descr: Collect all users for the company defaultCompany
  */  
  fetchUsers(company){

    const params = encodeURIComponent(company);
    return fetch(`/api/getUsersForCompany/?company=${params}`)
      .then(response => response.json())
  }


  selectMemberToMessage(member) {
    this.selected_member_to_message = member;
  }
  // Render the Information for each user 
  render() {
    return (
      <div className="row">
        <div className="mdl-grid">
          <div className="mdl-cell mdl-cell--3-col nav">
              <div className="direct-messages">
                  <h4> Direct Messages </h4>
                  <table>
                      <tbody>
                        <tr>
                          <td> </td> <td></td>
                          <td> 
                           <select  value={this.state.selected_member_status} onChange={this.selectMemberStatus}>
                             {this.state.member_statuses.map((status) => 
                                <option name={status} value={status}> {status} </option>
                              )}
                            </select>
                          </td> 
                        </tr>
                       { this.state.members.map((member) => 
                          <tr selectMemberToMessage>
                            <td> {member.profile_img_url} </td>
                            <td> {member.name} ({member.new_msg_count}</td>
                            <td> <img src={green_circle}/> </td>
                            </tr>
                        )}
                      </tbody>
                    </table>
              </div>
           </div>

            <div className="mdl-cell mdl-cell--8-col">
                <DirectMessage sender={this.state.currentUser} receiver={this.selected_member_to_message}> </DirectMessage>
            </div>


       </div>   
    </div> 
    );
  }
}


export default App;
