import React, { Component } from 'react';

class DirectMessage extends Component {
	 constructor() {
        super();
       this.state = {
      	messages: [{sender: 		"deepakabs",
      				senderName:		"Deepak ABS",
      				sender_profile_img_url: "abs",
      				sentAt: 		"Wed Nov 20, 2018 at 11",
      				message: 		"Nemam Amma Bhagavan Sharanam"
      			  }, 
      			  {sender: 		    "deepakabs",
      				senderName:		"Deepak ABS",
      				sender_profile_img_url: "abs",
      				sentAt: 		"Wed Nov 20, 2018 at 11",
      				message: 		"Nemam Amma Bhagavan Sharanam"
      			  }]
      }		  


    }
	render() {

		return (
			<div class="direct-message-history">
			 <table>
			 	<tbody>
			 	   {this.state.messages.map((message) => 
			 		<Message  message={message} currentUser={this.propscurrentUser}> </Message>
			 		)}
			 		</tbody>
			 </table>		
			</div>
		)
	}
}

class Message extends Component {

	constructor() {
		super();
	}
	render() {
	 	if (this.props.currentUser.username == this.props.message.sender) {

		return (
				// If message is sent by user style it differently
				<React.Fragment>
				<tr>
					<td className="sent-message"> 
						{this.props.message.message}
					</td>
					<td>
						{this.props.message.profile_image_url}
					</td>
				</tr>		

				<tr>
					<td>
						{this.props.message.date}
					</td>
					<td>
						YOU
					</td>
				</tr>		
				</React.Fragment>
	 		)
		}
