import React, { Component } from 'react';
import logo from './logo.svg';
import green_circle from './images/green_circle.png';
import './App.css';
import ReactDOM from 'react-dom';
import { DirectMessage } from './components/DirectMessage.js';
//import { Link, NavLink, Route } from 'react-router-dom' 




class App extends Component {
   constructor () {

    super()

    // Initialize local variable
    this.selected_member_to_message = "";
    // Declare DirectMessageUsers
    this.state = {
      // Set currentUser
      users:                          [],
      // Show loading spinner 
      fetching_direct_message_counts: true,

      // Indicate failure or success of request
      fetched_members_success:        false,

      // Display Current User at top
      currentUser: {},
      // Display 
      members: [],
      selected_member_status: "All",
      member_statuses:        ["All", "Online", "Away", "Offline"],
    }

     this.selectMemberToMessage = this.selectMemberToMessage.bind(this);         

  }

  // Fetch Users in Company
   componentDidMount() {
      // Show loading 
      this.setState({fetching_members: true, fetched_members_success: false});

      // Set currentUser 
      this.fetchUsers()
      .then((users) => {

        console.log ("Nemam Amma Bhagavan Sharanam -- users", users);
        // this.setState({
        //    // users:        [1,2,3],
        //   //currentUser:  users[0]
        //  // currentUser:  { ...this.state.currentUser, 
        //  //                name:             users[0].name, 
        //  //                username:         users[0].username, 
        //  //                company:          users[0].company,
        //  //                profile_img_url:  users[0].profile_img_url}
        // })

        // return this.fetchDirectMessageCountForUser(this.state.currentUser)

      })
      // .then((user_msg_counts) => {
      //   this.setState({
      //       members: user_msg_counts
      //   });
      //   this.fetching_direct_message_counts = false;

      // });
  //
     .catch((err) => {
        this.setState({fetching_members: false, fetched_members_success: false})
      });
  }   


  /*
    @name: fetchUsers
    @descr: Collect all users 
  */  
  fetchUsers(){
    let api     = "/api/getUsers/";

    return fetch(api)
    .then(response => response.json());
  }

  /*
    @name: fetchDirectMessageCountForUser
    @descr: Update state user_msg_counts with users and message counts
 */
  fetchDirectMessageCountForUser(currentUser) {

  // Username
  const username = encodeURIComponent(currentUser.username);

  // Company
  const company = encodeURIComponent(currentUser.username);

  let api = "/api/fetchDirectMessageCountForUser?username="  + username + "company=" + company;


  return fetch(api)
  .then(response =>  response.json())

 }

  selectMemberToMessage(member) {
    this.selected_member_to_message = member;
  }
  // Render the Information for each user 
  render() {
    return (
      <div className="row">
        <div className="mdl-grid">
          <div className="mdl-cell mdl-cell--3-col nav">
              <select class="mdl-textfield__input" value={this.state.currentUser.name}> 
                { this.state.users.map((user) => 
                  <option name={user.username} value={user.username}> user.name </option>
                )}
                </select>
              <div className="direct-messages">
                  <h4> Direct Messages </h4>
                  <table>
                      <tbody>
                        <tr>
                          <td> </td> <td></td>
                          <td> 
                           <select  class="mdl-textfield__input" value={this.state.selected_member_status} onChange={this.selectMemberStatus}>
                             {this.state.member_statuses.map((status) => 
                                <option name={status} value={status}> {status} </option>
                              )}
                            </select>
                          </td> 
                        </tr>
                       { this.state.members.map((member) => 
                          <tr onClick="this.selectMemberToMessage(member)">
                            <td> {member.profile_img_url} </td>
                            <td> {member.name} ({member.new_msg_count}</td>
                            <td> <img src={green_circle}/> </td>
                            </tr>
                        )}
                      </tbody>
                    </table>
              </div>
           </div>

            <div className="mdl-cell mdl-cell--8-col">
                <DirectMessage sender={this.state.currentUser} receiver={this.selected_member_to_message}> </DirectMessage>
            </div>


       </div>   
    </div> 
    );
  }
}


export default App;
