import React, { Component } from 'react';

export class DirectMessage extends Component {
	 constructor() {
        super();
       this.state = {
      	messages: [{sender: 		"deepakabs",
      				senderName:		"Deepak ABS",
      				profile_img_url: "abs",
      				sentAt: 		"Wed Nov 20, 2018 at 11",
      				message: 		"Nemam Amma Bhagavan Sharanam"
      			  }, 
      			  {sender: 		    "deepakabs",
      				senderName:		"Deepak ABS",
      				profile_img_url: "abs",
      				sentAt: 		"Wed Nov 20, 2018 at 11",
      				message: 		"Nemam Amma Bhagavan Sharanam"
      			  }]
      }		  


    }
	render() {

		return (
			<div class="direct-message-history">

		 	   {this.state.messages.map((message) => 
		 		<Message  message={message} currentUser={this.props.currentUser}> </Message>
		 		)}
			</div>
		)
	}
}

class Message extends Component {

	constructor() {
		super();
	}
	render() {
	 	if (this.props.currentUser.username == this.props.message.sender) {

		return (
				// If message is sent by user style it differently
				<React.Fragment>
				<ul className="sent-message-ul">
					<li className="sent-message"> 
						{this.props.message.message}
					</li>
					<li>
						{this.props.currentUser.profile_image_url}
					</li>
				</ul>		

				<ul className="sent-message-ul">
					<li>
						{this.props.message.createdAt}
					</li>
					<li>
						YOU
					</li>
				</ul>		
				</React.Fragment>

	 		)
		}

	}	
}
