import React, { Component } from 'react';

export class SendDirectMessage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: 'Enter the message to send'
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  sendMessage(event) {
    // Send the message using the API call
    //this.state.value;
    // Check if message is non empty

    var message = event;
        message = message.trim();

    if (message) {
      // Create directMessage Object
      var direct_message = {
                            message: message,
                            sender:  this.props.sender,
                            receiver: this.props.receiver,
                            id:       Date.now() + "_" + this.message.sender,   
                            status:   "sending"
      };

      // Update the messages object
      this.props.update_direct_messages(direct_message);


      // Set status as sending


    }   
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.sendMessage}>
        <label>

          <textarea value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Send" />
      </form>
    );
  }
}
