import React, { Component } from 'react';
export class DirectMessage extends Component {
	 constructor() {
        super();

        // Array to show date is displayed
       this.sent_dates_list = [];


       this.state = {
       	// Message to send
       	new_message: "",

       	// List of Messages
      	messages: [{sender: 		"deepakabs",
      				senderName:		"Deepak ABS",
      				profile_img_url: "abs",
      				sentAt: 		"Wed Nov 20, 2018 at 11",
      				message: 		"1"
      			  }, 
      			  {sender: 		    "deepakabs",
      				senderName:		"Deepak ABS",
      				profile_img_url: "abs",
      				sentAt: 		"Wed Nov 20, 2018 at 11",
      				message: 		"2"
      			  }
      			  ,
      			  {sender: 		"deepakabs",
      				senderName:		"Deepak ABS",
      				profile_img_url: "abs",
      				sentAt: 		"Wed Nov 20, 2018 at 11",
      				message: 		"3"
      			  }, 
      			  {sender: 		    "deepakabs",
      				senderName:		"Deepak ABS",
      				profile_img_url: "abs",
      				sentAt: 		"Wed Nov 20, 2018 at 11",
      				message: 		"4"
      			  }]
      }		  

        //          
        this.handleChange = this.handleChange.bind(this);
    	this.send_direct_messages = this.send_direct_messages.bind(this);       	

    }


    // Update Messages
    send_direct_messages() {

    	// 1. Create message object
    	var date 		= new Date();
    		date 		= date.toDateString();
    	var message 	= this.state.new_message;
        message = message.trim();

    	if (message) {

    	 	var timestamp 	= date.getTime();
     		 var direct_message = {
                            message:  message,
                            sender:   this.props.sender,
                            receiver: this.props.receiver,
                            id:       timestamp + "_" + this.props.sender,   

                            // Boolean indicating if message is sent
                            sent:   false,
                            // sentAt timestamp converted to hours and minutes
                            sentAt: "",

                            // Display date for the first message in a day
                            display_date: this.sent_dates_list.indexOf(date) ? false : true,
                            date_string:  date
      		};

      		// Update sent_dates_list
      		if (direct_message.display_date) this.sent_dates_list.push(date);


	    	// e.preventDefault();
	    	this.setState({
	    	  messages: this.messages.unshift(direct_message)
	    	});

	    	// Save the new message in database

      		// Update sent status 
  		}
  }

  // Update text field
 	 handleChange(event) {
   	 this.setState({new_message: event.target.value});
  	}	
    // Scroll to bottom
    scrollToBottom = () => {
	  this.messagesEnd.scrollIntoView({ behavior: "smooth" });
	}


	componentDidMount() {
	  this.scrollToBottom();
	}

	componentDidUpdate() {
	  this.scrollToBottom();
	}
	render() {

		return (
			<div className="direct-message-history-wrapper">
			<div className="direct-message-history">

		 	   {this.state.messages.map((message) => 
		 		<Message message = {message} sender={this.props.sender}  receiver={this.props.receiver}> </Message>
		 		)}
		 		 <div style={{ float:"left", clear: "both" }}
		             ref={(el) => { this.messagesEnd = el; }}>
		        </div>
			</div>

			<hr/>
			 <form onSubmit={this.sendDirectMessage}>
		        <label>

		          <textarea className="send-message-textarea" value={this.state.new_message} onChange={this.handleChange} />
		        </label>
		        <input type="submit" value="Send" />
		      </form>
			</div>
		)
	}
}

class Message extends Component {

	constructor() {
		super();
	}
	render() {
		let status = "";

		// If message is sent
		if (!this.props.message.sent){
			status = <span style={{color: '#423'}}> Sending... </span>
		}

		// If message is sent but unread
		else if(this.props.message.sent && !this.props.message.read) {
			status = <span> Sent at {this.props.message.sentAt} </span>
		}

		else if(this.props.message.sent && this.props.message.read) {
			status = <span> Seen at {this.props.message.sentAt} </span>
		}

	 	if (this.props.sender.username == this.props.message.sender) {

		return (
				// If message is sent by user style it differently
				<React.Fragment>
				<ul className="sent-message-ul">
					<li>
						{this.props.sender.profile_image_url}
						<br/>
						You
					</li>
					<li> 
						<span className="sent-message">
							{this.props.message.message}
						</span>
						<br/>
						<br/>

						{status}
					</li>

				</ul>		
				</React.Fragment>

	 		)
		}

	}	
}
