import React, { Component } from 'react';

export class SendDirectMessage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      message: 'Enter the message to send'
    };

    this.handleChange = this.handleChange.bind(this);
    this.sendMessage = this.sendMessage.bind(this);
  }

  handleChange(event) {
    this.setState({message: event.target.value});
  }

  sendMessage(event) {
    // Send the message using the API call
    //this.state.value;
    // Check if message is non empty
    event.preventDefault();
    var message = this.state.message;
        message = message.trim();

    if (message) {

      // Update the messages object
      this.props.send_direct_message(message);

      // Set status as sending
    }   
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.sendMessage}>
        <label>

          <textarea className="send-message-textarea" value={this.state.message} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Send" />
      </form>
    );
  }
}
